mapper.select:
select api.*, domain.name as domain_name, d.dept_name as dept_name, u.user_name as create_by_name
from bss_api api LEFT JOIN bss_domain  domain ON domain.id = api.domain_id
LEFT  JOIN sys_dept d on api.sys_dept_id = d.dept_id
LEFT  JOIN sys_user u on api.create_by = u.user_id
where 1=1

 and api.sys_dept_id = #{sysDeptId}
 and api.domain_id = #{domainId}
 and api.name like concat('%', #{name}, '%')

 and api.method = #{method}
 and api.is_online = #{isOnline}
 and api.is_read = #{isRead}
 and api.is_enable = #{isEnable}
 and api.recordable_count = #{recordableCount}
 and api.response_is_json = #{responseIsJson}

${params.dataScope}

	select api.*, domain.name as domain_name, dept.dept_name as dept_name
  from bss_api api LEFT JOIN bss_domain  domain ON domain.id = api.domain_id LEFT  JOIN sys_dept dept on api.sys_dept_id = dept.dept_id
  where api.api_id = #{apiId}

mapper.update:
update bss_api
sys_dept_id = #{sysDeptId}, domain_id = #{domainId}, name = #{name}, method = #{method}, is_online = #{isOnline}, is_read = #{isRead}, is_enable = #{isEnable}, recordable_count = #{recordableCount}, create_time = #{createTime}, create_by = #{createBy}, update_time = #{updateTime}, update_by = #{updateBy}, response_is_json = #{responseIsJson},
where api_id = #{apiId}
